module.exports = function(grunt) {
	require('load-grunt-tasks')(grunt);
	grunt.initConfig({
		pkg: grunt.file.readJSON('package.json'),
		babel: {
			options: {
				sourceMap: true,
				presets: ['es2015']
			},
			dist: {
				files: [{
					"cwd": "src/",
					"expand": true,
					"src": [
						'**/*.js'
					],
					"dest": "dist/",
					"ext": ".js"
				}]
			}
		}
	});


	grunt.registerTask('default', ['babel']);
};
